                <p>{title}</p>
                <p className="product__price">
                    <small>$</small>
               <strong>{price}</strong> 
                </p>
                <div className="product__rating">
                    {Array (rating)
                    .fill()
                    .map((_, i) =>(
                       
                    <p>  ‚≠ê     </p>
                    ))}

                </div>
            </div>
<img 
src={image}
alt="" 

/>

<button onClick={addToBasket}>Add to Basket </button>
        </div>
    );

}

export default Product
