         <Login />

       </Route>
     <Route path="/checkout">  
     <Header />

  <Checkout />
          </Route>


  <Route path="/">   
<Header />     
       <Home />
          </Route>
      {/* Header*/}
      {/* Home*/}
      </Switch>

    </div>
    </Router>

  );
}

export default App  ;
