import './Login.css'
import React, { useState }  from 'react'    ; 
import { Link, useHistory } from "react-router-dom"  ; 
import { auth } from "./firebase"  ; 
function Login() {
    const history = useHistory()   ; 
    const [email, setEmail] = useState('')  ; 
    const [password, setPassword] = useState('')  ; 

    const signIn = e => {
        e.preventDefault() ;
        auth
            .signInWithEmailAndPassword(email, password)
            .then(auth => {
                   history.push('/') 
               })
            .catch(error => alert(error.message))
    }

    const register = e => {
        e.preventDefault() ; 
        auth.createUserWithEmailAndPassword(email, password)
        .then((auth)=> {
                // if succes created a new user with email and password 
                console.log(auth) ; 
                if (auth) {
                    history.push('/') 
                }

            })
            .catch(error => alert(error.message))
  
    }
    return (
        <div className='Login'>
     <Link to='/'>
           <img
           className="Login__logo"
   src='https://www.carlogos.org/car-logos/tesla-logo-6000x2000.png'   /> 

</Link>
<div className='login__container' >
    <h1>Sign-in</h1>
    <form>
    <h5>Email</h5>
        <input type='text' value={email} 
        onChange={e =>setEmail(e.target.value)}    />
         <h5>Password</h5>
        <input type='password' value={password}  
        onChange={e => setPassword(e.target.value)}  />
      <button type='submit' onClick={signIn} className='login__signInButton'>Sign In</button>
                </form>
    <p>
 By  signing-in you agree to our condition of use & sale, please read it carefully     </p>
<button
onClick={register} className='login__registrationButton'>Create Your Amazon Account</button>
</div>
        </div>
    )
}

export default Login
