import React from "react";
import "./Header.css";
import SearchIcon from "@material-ui/icons/Search";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";
import { Link } from "react-router-dom";
import { useStateValue } from "./StateProvider";
import {auth} from "./firebase" ; 

function Header() {
    const [{ basket, user }, dispatch] = useStateValue () ; 
    const handleAuthentication = () => {
        if (user) {
            auth.signOut() ; 
        }
    }
    return (
        <div className="header">
            <Link to="/">
            <img className="header__logo"
            src="https://blog.radware.com/wp-content/uploads/2020/06/anonymous-960x638.jpg " /> 
            </Link>
       <div        className="header__search">
         
         
         
         
           <input  className="header__searchInput" type="text" />
 
            <SearchIcon className="header__searchIcon"/>
        </div> 



        <div className="header__nav">
       <Link to={!user && '/login'}>
        {/*One*/}<div onClick={handleAuthentication}
        className='header__option'>
            <span
            className='header__optionLineOne'>Hello Guest</span>

            <span
            className='header__optionLineTwo'>{user ?
            'Sign Out': 'Sign In'}  </span>
                </div>
                </Link>
       
        {/*Two*/}  
        <div className='header__option'>
            <span
            className='header__optionLineOne'>Return Orders</span>

            <span
            className='header__optionLineTwo'>& Orders  </span>
                </div>
    

      {/*Three*/} 
      <div className='header__option'>
            <span
            className='header__optionLineOne'>  Your </span>

            <span
            className='header__optionLineTwo'>Prime </span>
                </div>



  <Link to="/checkout">
  <div className="header__optionBasket">
       <ShoppingBasketIcon />
       <span className="header__optionLineTwo header__basketCount">{basket?.length}</span>
   </div>
  </Link>
        </div>
</div> 



   ); 
}

export default Header
